package com.syntax.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1st way to create a variable
		byte b = 10; //create a variable and assign value
		
		//2nd way to create a variable
		int i; //create a variable or declare a variable
		i = 100; //assign value
		
// we need to create a variable only once
		//then we can use as many time we want/need during the program
		
		//3rd way to create a variable
		int num1, num2, num3;
		
		num1=100; // assign value or initialize variable
		num2=200;
		num3=300;
		
		
		
	}

}
